export function getTimeRemaining(countDownDate) {
  let remaining;

  // Get today's date and time
  const now = new Date().getTime();

  // Find the distance between now and the count down date
  const distance = countDownDate - now;

  // Time calculations for days, hours, minutes and seconds
  let days = Math.floor(distance / (1000 * 60 * 60 * 24));
  let hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
  let minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
  let seconds = Math.floor((distance % (1000 * 60)) / 1000);

  if (distance >= 0) {
    remaining = {};
    remaining.delimitedByLetters =
      (days > 0 ? days + "d " : "") +
      (hours > 0 ? hours + "h " : "") +
      (minutes > 0 ? minutes + "m " : "") +
      (seconds > 0 ? seconds + "s " : "");
    remaining.delimitedByColons =
      (days > 0 ? (days < 10 ? formatDoubleZeroes(days) : days) : "00") +
      ":" +
      (hours > 0 ? formatDoubleZeroes(hours) : "00") +
      ":" +
      (minutes > 0 ? formatDoubleZeroes(minutes) : "00") +
      ":" +
      (seconds > 0 ? formatDoubleZeroes(seconds) : "00");
  }

  // If the count down is finished, write some text
  else {
    clearInterval(this);
    remaining = {
      delimitedByLetters: "Auction Ended",
      delimitedByColons: "Auction Ended"
    };
  }

  return remaining;
}

function formatDoubleZeroes(number) {
  return ("0" + number).slice(-2);
}
